#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
using namespace std;

class Solution {
public:
    int numSub(string s) {
        long long cur = 0;
        long long total = 0;
        s += "0";
        for (auto& c : s) {
            if (c == '0') {
                total += (1 + cur) * cur / 2 % (int)(1e9 + 7);
                cur = 0;
            } else {
                ++cur;
            }
        }
        return total;
    }
};

int main()
{
    string s = "";
    cout << Solution().numSub(s) << endl;
    return 0;
}